local edge_enum = enumeration["Edge-based bid/ask interval indicator"];
local nullvalue = edge_enum["Null"];

local ask_edge = current_user["Ask edge"];

if ask_edge == nil then
	return nullvalue;
end;

local large_ask_edge = current_user["Large ask edge"];
if large_ask_edge == nil then
	return nullvalue;
end;

local ask = instrument["Ask"];
if ask == nil then
	return nullvalue;
end;

local theo = instrument["Fair market price"];
if theo == nil then
    return nullvalue;
end;

if (ask <= theo - large_ask_edge) then
	return edge_enum["Large"];
elseif (ask <= theo - ask_edge) then
	return edge_enum["Medium"];
else
	return edge_enum["Short"];
end;

local edge_enum = enumeration["Edge-based bid/ask interval indicator"];
local nullvalue = edge_enum["Null"];

local bid_edge = current_user["Bid edge"];

if bid_edge == nil then
	return nullvalue;
end;

local large_bid_edge = current_user["Large bid edge"];
if large_bid_edge == nil then
	return nullvalue;
end;

local bid = instrument["Bid"];
if bid == nil then
	return nullvalue;
end;

local theo = instrument["Fair market price"];
if theo == nil then
    return nullvalue;
end;

if (bid >= theo + large_bid_edge) then
	return edge_enum["Large"];
elseif (bid >= theo + bid_edge) then
	return edge_enum["Medium"];
else
	return edge_enum["Short"];
end;
